Code1=""

# adds number 7 (could be any character that is not in Code1) to every vowel
# because the encryption is 1 char -> 2 char
# and vowels are constants

for v in 'AIUEO':
    Code1 = Code1.replace(v,v+'7')

# decrypting for six rounds
count = 1
while count < 7:
    CodeS = " ".join(Code1[i:i+2] for i in range(0, len(Code1), 2)) #adds space after every two chars
    Code2 = CodeS.replace('GN','G')
    Code3 = Code2.replace('NT','R')
    Code4 = Code3.replace('RH','N')
    Code5 = Code4.replace('TG','H')
    Code6 = Code5.replace('HH','T')
    Code1 = Code6.replace(" ","") #remove all spaces CodeC = Cleaned
    count += 1

CodeF = Code1.replace(" ","") #remove spaces
